sp_angular_provider.script:
function textEllipsisPopOver() {
  return {
template: '<span>{{displayText}}'+ 
			 '<p ng-if="multipleDisplayOption" style="color:red">Only one of the three options popOverText, popOverHtml or popOverTemplate can be used. </p>'+
			' <a href="javascript:void(0)"'+
			  'ng-if="popOverHtml && !multipleDisplayOption"'+
              'uib-popover-html="\'{{popOverHtml}}\'" '+
              'popover-placement="auto top" '+
              'popover-append-to-body="true" '+
              'popover-trigger="outsideClick"><i class="{{linkIcon}}"></i></a>'+
			  ' <a href="javascript:void(0)"'+
					  'ng-if="popOverText && !multipleDisplayOption"'+
					  'uib-popover="{{popOverText}}" '+
					  'popover-placement="auto right" '+
					  'popover-append-to-body="true" '+
					  'popover-trigger="outsideClick"><i class="{{linkIcon}}"></i></a>'+
				' <a href="javascript:void(0)"'+
					  'ng-if="popOverTemplate && !multipleDisplayOption"'+
					  'uib-popover-template="\'{{popOverTemplate}}\'" '+
					  'popover-placement="auto right" '+
					  'popover-append-to-body="true" '+
					  'popover-trigger="outsideClick"><i class="{{linkIcon}}"></i></a>'+
				  '</span>',
	restrict: 'E',
	replace:true,
	scope :     {
		textLength:  "@",
		text:  "@",
		popOverText : "@",
		popOverHtml : "@",
		popOverTemplate : "@",
		iconClass : "@"
	},
	controller: function($scope) {
			$scope.multipleDisplayOption = false;
			$scope.linkIcon = $scope.iconClass?$scope.iconClass:'fa fa-ellipsis-h';
			var optionsSelected = 0;
			if($scope.popOverText)optionsSelected+=1;
			if($scope.popOverHtml)optionsSelected+=1;
			if($scope.popOverTemplate)optionsSelected+=1;
			if(optionsSelected>1)$scope.multipleDisplayOption=true;

			var eleText = $scope.text;
			var eleTextLength = $scope.textLength;
			var showHtml = $scope.popOverHtml?true:false;
			var length = parseInt(eleTextLength || 0) || 30,
              text = eleText || '';
            if (text && text.length > length) {
              $scope.displayText = text.substring(0, length);

            } else {
              $scope.displayText = text;
            }
	}
  };
}
