sp_angular_provider.script:
function textEllipsisToggle() {
  return {
template: '<span>{{displayText}}'+ 
			' <a ng-if="showLessLink" href="javascript:void(0)" ng-click="toggleTruncatedText()"> less</a>'+
			' <a ng-if="showMoreLink" href="javascript:void(0)" ng-click="toggleTruncatedText()">... more</a>'+
			'</span>',
	restrict: 'E',
	replace:true,
	scope :     {
		textLength:  "@",
		text:  "@",
	},
	controller: function($scope) {
			//$scope.showLink = true;
			$scope.showMoreLink = true;
			$scope.showLessLink = false;

			$scope.toggleTo='fullDisplay';
			var eleText = $scope.text;
			var eleTextLength = $scope.textLength;
			var length = parseInt(eleTextLength || 0) || 30,
              text = eleText || '';
            if (text && text.length > length) {
			  $scope.fullText=text;
              $scope.displayText = text.substring(0, length);
			  $scope.partialText=text.substring(0, length);
			  $scope.toggleTo='fullDisplay';

            } else {
              $scope.displayText = text;
			  //$scope.showLink = false;
			  $scope.showMoreLink = false;
			  $scope.showLessLink = false;
			  $scope.toggleTo='trunc';
            }

			$scope.toggleTruncatedText = function() {
				if($scope.toggleTo=='fullDisplay'){
					$scope.displayText = $scope.fullText;
					$scope.toggleTo='trunc'
					$scope.showMoreLink = false;
					$scope.showLessLink = true;
				}else if($scope.toggleTo=='trunc'){
					$scope.displayText =$scope.partialText;
					$scope.toggleTo='fullDisplay'
					$scope.showMoreLink = true;
					$scope.showLessLink = false;

				}
			};
		/*
		$scope.expandText = function() {
					$scope.displayText = $scope.fullText;
					$scope.showLink = false;
			};
			*/
	}
  };
}
